import React from "react";
import axios from "axios";

class Form extends React.Component {
  submitProfile = (e) => {
    e.preventDefault();
    axios
      .post({
        method: "post",
        url: "http://localhost:4000/mern/profiles",
        data: {
          firstName: this.refs.firstName.value,
          email: this.refs.email.value,
          phone: this.refs.phone.value,
          // lastName: this.refs.lastName.value,
        },
      })
      .then((response) => {
        console.log(response);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  render() {
    return (
      <div className="row">
        <h1 className="center">Add a new profile </h1>
        <form className="col s12" onSubmit={this.submitProfile}>
          <div className="row">
            <div className="input-field col s6">
              <input id="firstName" ref="firstName" type="text" />
              <label htmlFor="firstName">First Name</label>
            </div>
            <div className="input-field col s6">
              <input id="lastName" ref="lasttName" type="text" />
              <label htmlFor="lastName">Last Name</label>
            </div>
          </div>
          <div className="row">
            <div className="input-field col s6">
              <input id="email" ref="email" type="text" />
              <label htmlFor="email">Email</label>
            </div>
            <div className="input-field col s6">
              <input id="phone" ref="phone" type="text" />
              <label htmlFor="phone">Phone</label>
            </div>
          </div>
          <button
            className=" btn waves-effect waves-lght"
            type="submit"
            name="action"
          >
            Add Profile
          </button>
        </form>
      </div>
    );
  }
}

export default Form;
