import React from "react";
import logo from "../logo.svg";
import "./App.css";
import List from "./models/List";
import Profile from "./models/Profile";
import Form from "./models/Form";

class App extends React.Component {
  state = {
    profiles: [],
    currentProfile: {},
  };

  componentDidMount() {
    const url = "http://localhost/mern/profiles";
    axios
      .get(url)
      .then((res) => {
        this.setState({ profiles: res.data });
      })
      .catch((err) => {
        console.log(err);
      });
  }

  updateCurrentModel = (item) => {
    this.setState({
      currentProfile: item,
    });
  };

  render() {
    return (
      <div className="App">
        <div className="row">
          <div className="col s12">Menu</div>
        </div>
        <div className="row">
          <div className="col s3">
            <List
              models={this.state.profiles}
              updateCurrentProfile={this.updateCurrentProfile}
            />
          </div>
          <div className="col s9">
            <Profile />
          </div>
        </div>
        <div className="row">
          <div className="col s12">
            <Form />
          </div>
        </div>
      </div>
    );
  }
}

export default App;
