import React from "react";
import axios from "axios";

class Form extends React.Component {
  submitProfile = (e) => {
    const formData = new FormData(e.target);
    e.preventDefault();
    axios({
      method: "post",
      url: "http://localhost:4000/mern/profiles",
      data: formData,
      // data: {

      //   firstName: e.target.firstName.value,
      //   email: e.target.email.value,
      //    phone: e.target.phone.value,
      //    lastName: e.target.lastName.value,
      // },
    })
      .then((res) => {
        console.log(res);
      })
      .catch((err) => {
        console.error(err);
      });
  };

  render() {
    return (
      <div className="row">
        <h1 className="center">Add a new profile </h1>
        <form
          action="/profile"
          method="post"
          enctype="multipart/form-data"
          className="col s12"
          onSubmit={this.submitProfile}
        >
          <div className="row">
            <div className="input-field col s6">
              <input id="firstName" type="text" />
              <label htmlFor="firstName">First Name</label>
            </div>
            <div className="input-field col s6">
              <input id="lastName" type="text" />
              <label htmlFor="lastName">Last Name</label>
            </div>
          </div>
          <div className="row">
            <div className="input-field col s6">
              <input id="email" type="text" />
              <label htmlFor="email">Email</label>
            </div>
            <div className="input-field col s6">
              <input id="phone" type="text" />
              <label htmlFor="phone">Phone</label>
            </div>
          </div>
          <div class="row">
            <div class="col s12">
              <div class="file-field input-field">
                <div class="btn">
                  <span>File</span>
                  <input id="photos" name="photos" type="file" multiple />
                </div>
                <div class="file-path-wrapper">
                  <label for="photos" class="sr-only">
                    Photos to upload
                  </label>
                  <input
                    id="photos_names"
                    name="photos_names"
                    class="file-path validate"
                    type="text"
                    placeholder="Upload one or more files"
                  />
                </div>
              </div>
            </div>
          </div>
          <button
            className=" btn waves-effect waves-lght"
            type="submit"
            name="action"
          >
            Add Profile
          </button>
        </form>
      </div>
    );
  }
}

export default Form;
