import React from "react";
import axios from "axios";

import "./App.css";
import List from "./profiles/List";
import Profile from "./profiles/Profile";
import Form from "./profiles/Form";
import ErrorBoundary from "./ErrorBoundary";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      profiles: [],
      currentProfile: {},
    };
    this.updateCurrentProfile = this.updateCurrentProfile.bind(this);
  }

  componentDidMount() {
    const url = "http://localhost:4000/mern/profiles";
    axios
      .get(url)
      .then((Response) => {
        this.setState({ profiles: Response.data });
      })
      .catch((err) => {
        console.log(err);
      });
  }

  updateCurrentProfile(item) {
    this.setState({
      currentProfile: item,
    });
  }

  render() {
    return (
      <div className="App">
        <div className="row">
          <div className="col s12">Menu</div>
        </div>
        <div className="row">
          <div className="col s3">
            <ErrorBoundary>
              <List
                profiles={this.state.profiles}
                updateCurrentProfile={this.updateCurrentProfile}
              />
            </ErrorBoundary>
          </div>
          <div className="col s9">
            <Profile />
          </div>
        </div>
        <div className="row">
          <div className="col s12">
            <Form />
          </div>
        </div>
      </div>
    );
  }
}

export default App;
